var Collection = require('../');

describe('Collection', function(){
  var collection;
  
  beforeEach(function(){
    collection = new Collection;
  });
  
  describe('.myMethod', function(){
    // example sync test
    it('should <description of behavior>', function() {
      collection.myMethod();
    });
    
    // example async test
    it('should <description of behavior>', function(done) {
      setTimeout(function () {
        collection.myMethod();
        done();
      }, 0);
    });
  });
});