var Model = require('../');

describe('Model', function(){
  var model;
  
  beforeEach(function(){
    model = new Model;
  });
  
  describe('.myMethod', function(){
    // example sync test
    it('should <description of behavior>', function() {
      model.myMethod();
    });
    
    // example async test
    it('should <description of behavior>', function(done) {
      setTimeout(function () {
        model.myMethod();
        done();
      }, 0);
    });
  });
});