var Connection = require('../');

describe('Connection', function(){
  var connection;
  
  beforeEach(function(){
    connection = new Connection;
  });
  
  describe('.myMethod', function(){
    // example sync test
    it('should <description of behavior>', function() {
      connection.myMethod();
    });
    
    // example async test
    it('should <description of behavior>', function(done) {
      setTimeout(function () {
        connection.myMethod();
        done();
      }, 0);
    });
  });
});