var Resource = require('../');

describe('Resource', function(){
  var resource;
  
  beforeEach(function(){
    resource = new Resource;
  });
  
  describe('.myMethod', function(){
    // example sync test
    it('should <description of behavior>', function() {
      resource.myMethod();
    });
    
    // example async test
    it('should <description of behavior>', function(done) {
      setTimeout(function () {
        resource.myMethod();
        done();
      }, 0);
    });
  });
});