var Store = require('../');

describe('Store', function(){
  var store;
  
  beforeEach(function(){
    store = new Store;
  });
  
  describe('.myMethod', function(){
    // example sync test
    it('should <description of behavior>', function() {
      store.myMethod();
    });
    
    // example async test
    it('should <description of behavior>', function(done) {
      setTimeout(function () {
        store.myMethod();
        done();
      }, 0);
    });
  });
});